import axios from '../axios';
import Types from '../../../types/index';

// 新增文章
function addArticle(params: Types.AddArticle) {
    return axios.post(`article/addArticle`, params);
}

// 获取文章
const getArticle = (): object => {
    return axios.get(`article/getArticle`);
};

// 发表回复
const sendReply = (params: any): object => {
    return axios.post(`/sendReply`, params);
};

const article = {
    addArticle,
    getArticle,
    sendReply,
};
export default article;
