@keyframes rotate {
	from {
		transform: translate(-50%, -1%) rotateX(73deg) rotateZ(0);
	}
	to {
		transform: translate(-50%, -1%) rotateX(73deg) rotateZ(360deg);
	}
}

.ed-content {
	height: 100vh;
	width: 100vw;
	background: url(../../assets/img/back.jpg) no-repeat center center;
	background-size: cover;
	position: relative;
	padding-top: 60px;
	perspective: 9000px;
	overflow: hidden;
	color: #fff;

	.ed-top {
		position: relative;
		display: flex;
		align-items: center;
		height: 51px;
		font-size: 18px;
		z-index: 1;
		.yx-logo {
			flex: 1;
			margin-left: 3.75vw;
			height: 51px;
			background: url(../../assets/login_img/yx_logo.png) no-repeat left center;
			background-size: contain;
		}
		.login-out {
			padding-right: 65px;
			cursor: pointer;
			font-weight: 100;
		}
		.full-screen {
			padding-right: 20px;
		}
	}

	.ed-name {
		text-align: center;
		font-size: 40px;
	}

	.sub-title {
		position: absolute;
		left: 50%;
		top: 35%;
		transform: translateX(-50%);
		z-index: 5;
		font-size: 25px;
		color: #fff;
		font-weight: 400;
	}

