	.page-count {
		position: absolute;
		top: 40%;
		left: 50%;
		z-index: 10;
		transform: translateX(-50%);
		display: flex;
		cursor: pointer;
		align-items: flex-end;
		.number-column {
			width: 55px;
			height: 68px;
			padding: 3px;
			padding-top: 10px;
			border-radius: 5px;
			overflow: hidden;
			background: url(../../assets/img/number_box.png) no-repeat center center;
			background-size: contain;
			box-shadow: 0 0 0 0 #5ac5fd;
			transition: box-shadow 0.5s ease;
		}
	}

	.orbit {
		position: absolute;
		width: 80vw;
		height: 80vw;
		left: 50%;
		z-index: 1;
		background: url(../../assets/img/orbit.png) no-repeat center center;
		background-size: contain;
		animation: rotate 10s infinite linear;
	}

	.app-enter {
		position: absolute;
		z-index: 18;
	}

	.app-enter>.link-container {
		padding: 0.3em;
		font-size: 20px;
		display: flex;
		justify-content: center;
		align-items: center;
		text-align: center;
		border-radius: 5px;
		cursor: pointer;
		font-weight: 300;
	}

	.app-enter>.link-container:hover {
		color: #fff;
		text-decoration: none;
		font-weight: 300;
	}

	.app-enter>.shadow {
		position: absolute;
		border-radius: 3px;
		z-index: -1;
	}

	.app-enter>.arrow {
		position: absolute;
		bottom: 0;
		left: 50%;
		transform: translate(-50%, 50%) rotate(45deg);
		z-index: 5;
	}

	.app-enter>.group {
		position: absolute;
		left: 50%;
		transform: translateX(-50%);
		font-size: 14px;
		color: #fff;
		white-space: nowrap;
		font-weight: 300;
	}

	.app-enter>.point {
		position: absolute;
		left: 50%;
		transform: translateX(-50%);
		width: 30px;
		height: 30px;
		border-radius: 50%;
	}

	.point>.circle {
		position: absolute;
		left: 50%;
		top: 50%;
		transform: translate(-50%, -50%);
		width: 71%;
		height: 71%;
		border-radius: 50%;
	}

	.point>.circle:before {
		position: absolute;
		left: 50%;
		top: 50%;
		transform: translate(-50%, -50%);
		content: '';
		display: block;
		width: 67%;
		height: 67%;
		border-radius: 50%;
	}

	.app-enter.zhxy .circle:before,
	.app-enter.pkxk .circle:before,
	.app-enter.qywx .circle:before {
		background: rgba(30, 173, 249, 0.5);
	}

	.app-enter.bwtk .circle:before {
		background: rgba(174, 131, 252, 0.5);
	}

	.app-enter.jydsj .circle:before,
	.app-enter.ksyj .circle:before,
	.app-enter.cjfx .circle:before {
		background-color: rgba(254, 218, 0, 0.5);
	}

	.app-enter.zhszpj .circle:before,
	.app-enter.zysygh .circle:before,
	.app-enter.zbpkxk .circle:before {
		background-color: rgba(43, 209, 199, 0.5);
	}


	.point>.circle:after {
		position: absolute;
		left: 50%;
		top: 50%;
		transform: translate(-50%, -50%);
		content: '';
		display: block;
		width: 33%;
		height: 33%;
		border-radius: 50%;
	}

	.app-enter.zhxy .circle:after,
	.app-enter.pkxk .circle:after,
	.app-enter.qywx .circle:after {
		background: rgb(30, 173, 249);
	}

	.app-enter.bwtk .circle:after {
		background: rgb(174, 131, 252);
	}

	.app-enter.jydsj .circle:after,
	.app-enter.ksyj .circle:after,
	.app-enter.cjfx .circle:after {
		background-color: rgb(254, 218, 0);
	}

	.app-enter.zhszpj .circle:after,
	.app-enter.zysygh .circle:after,
	.app-enter.zbpkxk .circle:after {
		background-color: rgb(43, 209, 199);
	}

	.app-enter:hover>.link-container {
		color: #fff !important;
	}

	.app-enter.zhxy:hover>.link-container,
	.app-enter.zhxy:hover>.arrow,
	.app-enter.pkxk:hover>.link-container,
	.app-enter.pkxk:hover>.arrow,
	.app-enter.qywx:hover>.link-container,
	.app-enter.qywx:hover>.arrow {
		background-color: rgb(30, 173, 249) !important;
	}
	.app-enter.zhxy:hover>.shadow,
	.app-enter.pkxk:hover>.shadow,
	.app-enter.qywx:hover>.shadow {
		background-color: rgba(30, 173, 249, 0.1);
	}

	.app-enter.bwtk:hover>.link-container,
	.app-enter.bwtk:hover>.arrow {
		background-color: rgb(174, 131, 252) !important;
	}
	.app-enter.bwtk:hover>.shadow {
		background-color: rgba(174, 131, 252, 0.1);
	}

	.app-enter.jydsj:hover>.link-container,
	.app-enter.jydsj:hover>.arrow,
	.app-enter.ksyj:hover>.link-container,
	.app-enter.ksyj:hover>.arrow,
	.app-enter.cjfx:hover>.link-container,
	.app-enter.cjfx:hover>.arrow {
		background-color: rgb(254, 218, 0) !important;
	}
	.app-enter.jydsj:hover>.shadow,
	.app-enter.ksyj:hover>.shadow,
	.app-enter.cjfx:hover>.shadow {
		background-color: rgba(254, 218, 0, 0.1);
	}

	.app-enter.zhszpj:hover>.link-container,
	.app-enter.zhszpj:hover>.arrow,
	.app-enter.zysygh:hover>.link-container,
	.app-enter.zysygh:hover>.arrow,
	.app-enter.zbpkxk:hover>.link-container,
	.app-enter.zbpkxk:hover>.arrow {
		background-color: rgb(43, 209, 199) !important;
	}
	.app-enter.zhszpj:hover>.shadow,
	.app-enter.zysygh:hover>.shadow,
	.app-enter.zbpkxk:hover>.shadow {
		background-color: rgba(43, 209, 199, 0.1);
	}


	/* 模态框 */
	.modal {
		position: fixed;
		left: 0;
		top: 0;
		width: 100vw;
		height: 100vh;
		background: rgba(0,0,0,0.65);
		color: #fff;
		z-index: 50;
	}

	.modal>.hang {
		top: -100%;
	}

	.modal>.slide {
		top: 20%;
	}

	.modal>.modal-box {
		position: absolute;
		left: 50%;
		transform: translateX(-50%);
		width: 570px;
		border-radius: 5px;
		transition: all 0.3s ease;
		box-shadow: 0 0 30px rgba(255,255,255,0.3) inset;
	}

	.modal-close {
		position: absolute;
		right: 0;
		top: 0;
		display: inline-block;
		width: 30px;
		height: 30px;
		color: #FFF;
		border-radius: 50%;
		text-align: center;
		font-size: 30px;
		line-height: 30px;
		transform: translate(50%, -50%) rotate(45deg);
		cursor: pointer;
	}

	.modal-header {
		text-align: center;
		height: 62px;
		font-weight: 300;
		line-height: 61px;
		font-size: 24px;
		color: #fff;
	}

	.modal-passage {
		font-size: 14px;
		line-height: 2;
		margin: 0;
		text-indent: 2em;
	}

	.modal-footer {
		padding: 0 20px;
	}

	.modal-footer>p {
		font-size: 14px;
		line-height: 2;
	}

	.modal-footer .btn {
		display: inline-block;
		padding: 10px 55px;
		border-radius: 5px;
		background: #2298E6;
		color: #fff;
		font-size: 20px;
		font-weight: 300;
		cursor: pointer;
		text-decoration: none;
	}

	.modal-footer .btn:hover {
		opacity: 0.9;
	}

	.modal-icon {
		display: inline-block;
		width: 26px;
		height: 26px;
		margin-right: 10px;
		vertical-align: middle;
	}

	.modal-icon-phone {
		background: url(../../assets/img/contact_phone.png);
	}

	.modal-icon-mail {
		background: url(../../assets/img/contact_mail.png);
	}

	/* 数字滚动样式 */
	.paper-count {
		position: absolute;
		top: 40%;
		left: 50%;
		z-index: 10;
		transform: translateX(-50%);
		display: flex;
		cursor: pointer;
		align-items: flex-end;
	}

	.paper-count .comma {
		font-size: 60px;
		font-family: 'Aldrich',sans-serif;
		color: #fff;
	}

	.paper-count:hover .number-column {
		box-shadow: 0 0 12px 3px #5ac5fd;
	}

	.paper-count .number-column {
		width: 55px;
		height: 68px;
		padding: 3px;
		padding-top: 10px;
		border-radius: 5px;
		overflow: hidden;
		background: url(../../assets/img/number_box.png) no-repeat center center;
		background-size: contain;
		box-shadow: 0 0 0 0 #5ac5fd;
		transition: box-shadow 0.5s ease;
	}

	.numbers-elevator {
		transition: all 2s ease;
	}

	.numbers-elevator>.number-cell {
		text-align: center;
		vertical-align: base;
		width: 49px;
		height: 62px;
		line-height: 62px;
		font-family: 'Aldrich',sans-serif;
		font-size: 60px;
		color: #fff;
	}

	.ele-0 {
		margin-top: 0;
	}
	.ele-1 {
		margin-top: -62px;
