import {Vue, Component,Prop} from 'vue-property-decorator'
import VmMarkdownDropdown from './vm-markdown-dropdown.vue'
import insertText from './utils/index.js'
@Component({components:{VmMarkdownDropdown}})
export default class Name extends Vue {
    @Prop({default: '#232323'})hoverColor: string;

    insertText(string:string) {
        let content:any = document.querySelector('.vm-markdown-content');
        insertText(content, string);
        this.$emit('textChange', content.value)
    }

    get filterColor() {
        if (this.hoverColor === '#fafbfc') {
            return '#232323'
        } else {
            return this.hoverColor
        }
    }

    mounted() {
        let table:any = document.querySelector('.vm-markdown-table');
        let length = 24;
        let x = 0, y = 0;
        for (let i:number = 0; i < length; i++) {
            const setx:any = i % 6 + 1;
            let sety:any = i / 6 + 1;
            let li = document.createElement('li');
            li.setAttribute('data-x', setx);
            li.setAttribute('data-y', sety);
            table.appendChild(li);
        }

        table.addEventListener('mouseover', (evt:any) => {
            if (evt.target.tagName === 'LI') {
                x = evt.target.getAttribute('data-x');
                y = evt.target.getAttribute('data-y');
                let lis = table.querySelectorAll('li');
                for (let i = 0; i < lis.length; i++) {
                    lis[i].style.backgroundColor = '#e0e0e0';
                    if (lis[i].dataset.x <= x && lis[i].dataset.y <= y) {
                        lis[i].style.backgroundColor = this.filterColor
                    }
                }
            }
        });
        table.addEventListener('click', (evt:any) => {
            if (x && y) {
                let th = '| Head ';
                let td = '| Data ';
                let tl = '| ---  ';
                let str = '';
                let ths = '',
                    tls = '',
                    tds = '';
                for (let i = 0; i < x; i++) {
                    ths = ths.concat(th);
                    tls = tls.concat(tl);
                }
                for (let j = 0; j < y; j++) {
                    for (let k = 0; k < x; k++) {
                        tds = tds.concat(td)
                    }
                    tds += ' |\n'
                }
                ths += ' |\n';
                tls += ' |\n';
                str += '\n' + ths + tls + tds;
                this.insertText(str)
            }
        })
    }
}
